import {useState} from "react";

function App() {
  const list = [
    {id:'1', title:'Sasha', inPortal:false},
    {id:'2', title:'Masha', inPortal:false},
    {id:'3', title:'Natasha', inPortal:false},
    {id:'4', title:'Gosha', inPortal:false},
    {id:'5', title:'Misha', inPortal:false},
  ]
  const hideElement = (id) => {
    list.map(el=>{
      if (el.id === id) {
        el.inPortal = !el.inPortal
      }
      return el
    })
  }

  hideElement(3)
  return (
    <div>
      <ul>
        {list.filter(el=>!el.inPortal).map(el=><li key={el.id}>{el.title}</li>)}
      </ul>
<button onClick={()=>{hideElement(1)}}>тыц</button>
    </div>
  );
}

export default App;
