import {useState} from "react";
import {Portal} from "./Portal";

function App() {
  const [list, setList] = useState([
    {id:'1', title:'Sasha', inPortal:false},
    {id:'2', title:'Masha', inPortal:false},
    {id:'3', title:'Natasha', inPortal:false},
    {id:'4', title:'Gosha', inPortal:false},
    {id:'5', title:'Misha', inPortal:false},
  ])
  const [portal, setPortal] = useState({isShow: false, title: null})

  const hideElement = (id, title) => {
    let newList = list.map(el=>{
      if (el.id === id) {
        el.inPortal = !el.inPortal
      }
      setPortal(prev=>{return{...prev, isShow: true, title: title}})
      return el
    })
    setList(newList)
  }

  const showElement = (title) => {
    let newList = list.map(el=>{
      if (el.title === title) {
        el.inPortal = false
      }
      return el
    })
    setPortal(pr=>{return{...pr, isShow: false, title: null}})
    setList(newList)
  }

  return (
    <div>
      <ul>
        {list.filter(el=>!el.inPortal).map(el=><li key={el.id} onClick={hideElement.bind(null, el.id, el.title)}>{el.title}</li>)}
      </ul>

      {/*{portal.isShow && */}
      {/*<Portal title={portal.title}>*/}
      {/*  <div>*/}
      {/*    <p>{portal.title}</p>*/}
      {/*    <div>*/}
      {/*      <button onClick={showElement.bind(null, portal.title)}>Return</button>*/}
      {/*      <button>Close</button>*/}
      {/*    </div>*/}
      {/*  </div>*/}
      {/*</Portal>}*/}


    </div>
  );
}

export default App;
